mapper.update:
UPDATE TB_INDEX_CONFIG
SET is_deleted=1,UPDATE_time=now() WHERE is_deleted=0 AND config_id IN

    #{id}

mapper.select:
SELECT

FROM TB_INDEX_CONFIG


        AND config_type = #{configType}

    AND is_deleted = 0

order by config_rank desc

    limit #{start},#{limit}

SELECT count(*) FROM TB_INDEX_CONFIG


        AND config_type = #{configType}

    AND is_deleted = 0
