mapper.select:
SELECT

FROM TB_USER
    WHERE login_name = #{loginName} 
    AND password_md5 = #{password} 
    AND is_deleted = 0

mapper.update:
UPDATE TB_USER
    SET locked_flag=#{lockStatus} 
    WHERE user_id IN

        #{id}

mapper.select:
SELECT

FROM TB_USER
    WHERE 1=1

        AND login_name = #{loginName}

    ORDER BY create_time DESC

        LIMIT #{start},#{limit}

SELECT count(*) FROM TB_USER
WHERE 1=1

    AND login_name = #{loginName}
